interface Props {
  preferences: UserPreferences;
}

export function SubjectsSection({ preferences }: Props) {
  const [selectedSubject, setSelectedSubject] = useState<Subject | null>(null);

  return (
    <section id="features" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-4">Our Subjects</h2>
        <p className="text-xl text-gray-600 text-center mb-12 max-w-2xl mx-auto">
          Explore our wide range of subjects, each adapted for different learning needs
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {subjects.map((subject) => (
            <SubjectCard
              key={subject.title}
              {...subject}
              onClick={() => setSelectedSubject(subject)}
            />
          ))}
        </div>
      </div>

      <ContentModal
        isOpen={!!selectedSubject}
        onClose={() => setSelectedSubject(null)}
      >
        {selectedSubject && (
          <SubjectContent
            title={selectedSubject.title}
            content={selectedSubject.content}
            preferences={preferences}
          />
        )}
      </ContentModal>
    </section>
  );
}
